1 = 1 AND pu.create_user = #{createUser} AND pu.udf_name like CONCAT('%',#{udfName},'%') AND pu.udf_type in
#{item}
) a JOIN (SELECT udf_id,description,path,register_format,use_format,bml_resource_version FROM linkis_ps_udf_version as puv1 WHERE bml_resource_version = (SELECT max(puv2.bml_resource_version) FROM linkis_ps_udf_version as puv2 WHERE puv1.udf_id =puv2.udf_id)) b ON a.id=b.udf_id order by id desc

